// Set a number of profile values
TGFProfile.prototype.setValues = function(values) {
	"use strict";
	var key;
	if (typeof values !== "object") { return; }
	for (key in values) {
		if (values.hasOwnProperty(key)) { this[key] = values[key]; }
	}
};


