// Processing required when preparing the component for use
tgfComponents.Timer.prototype.doPrepare = function() {
	"use strict";
	var i;
	this.reset();
	// Add the child components
	for (i = 0; i < this.components.length; i += 1) {
		this.addComponent(this.components[i]);
	}
};


