		height = this.height;

		if (height <= 0) {
			height = hh;
		}


		var lineInfo = this.font.lineInfo();
		var lines = lineInfo.lines;
		var lineHeight = lineInfo.lineHeight;
		
		var areaHeight = lineHeight * lines.length;
		
		this._yOffset = height / 2 - areaHeight/2;
