        this.image.setDimensions(this.getWidth(), this.getHeight());

        this.image.add(this.baseContainer);

        this.image.setScale(this.xScale, this.yScale);
        if(this.onClick){
            this.image.setCallback(function () {
                if(!self.isActive || !self.isVisible) { return; }
                self.onClick();
            }, true);
        }

